(()=>{const e=document.querySelectorAll("section[id]");window.addEventListener("scroll",(function(){let t=window.pageYOffset;e.forEach((e=>{const o=e.offsetHeight,i=e.getBoundingClientRect().top+window.pageYOffset-50;sectionId=e.getAttribute("id"),t>i&&t<=i+o?document.querySelector(".navigation a[href*="+sectionId+"]").classList.add("active"):document.querySelector(".navigation a[href*="+sectionId+"]").classList.remove("active")}))}))})();